UPDATE evacuation_zones SET evacuated = CASE 
  WHEN status = 'evacuated' THEN population 
  WHEN status = 'evacuating' THEN FLOOR(population * 0.7)
  WHEN status = 'warning' THEN FLOOR(population * 0.3)
  ELSE 0 END;

